attrs.script:
api_fqdn = '@@{PC_API_FQDN}@@'
api_user = '@@{PC_API_USER}@@'
api_pass = '@@{PC_API_PASS}@@'

headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}
url = "https://{}:9440/api/nutanix/v3/categories/list".format(api_fqdn)
payload = {"kind": "category","sort_attribute": "name","sort_order": "ASCENDING","length": 1000,"offset": 0}
output=[]
category_name = "@@{CategoryName}@@"

# Check if the category exists
resp = urlreq(url, verb='POST', auth='BASIC', user=api_user, passwd=api_pass, params=json.dumps(payload), headers=headers)
if resp.ok:
    result = json.loads(resp.content)
    for x in result['entities']:
      if x['name'].lower() == category_name.lower():
        output = x['name']
    if not output:
      print "ERROR: The specified category does not exist."
      exit(0)
else:
    error_details = resp.json()
    print('ERROR: Code {} - {}').format(error_details['code'],error_details['message_list'][0]['message'])
    exit(1)

# check how many vm entities are assigned to the category
url = "https://{}:9440/api/nutanix/v3/groups".format(api_fqdn)
payload = {"entity_type":"mh_vm","query_name":"eb:data-1698359165644","grouping_attribute":" ","group_count":3,"group_offset":0,"group_attributes":[],"group_member_count":1000,"group_member_offset":0,"group_member_sort_attribute":"vm_name","group_member_sort_order":"ASCENDING","group_member_attributes":[{"attribute":"vm_name"},{"attribute":"num_vcpus"},{"attribute":"memory_size_bytes"},{"attribute":"ip_addresses"},{"attribute":"cluster_name"},{"attribute":"hypervisor_type"},{"attribute":"guest_os_name"},{"attribute":"ngt.installed_version"},{"attribute":"project_name"},{"attribute":"owner_username"},{"attribute":"project_reference"},{"attribute":"owner_reference"},{"attribute":"categories"},{"attribute":"cluster"},{"attribute":"state"},{"attribute":"message"},{"attribute":"reason"},{"attribute":"is_cvm"},{"attribute":"is_acropolis_vm"},{"attribute":"is_live_migratable"},{"attribute":"gpus_in_use"},{"attribute":"network_security_rule_id_list"},{"attribute":"zone_type"},{"attribute":"vm_annotation"},{"attribute":"vm_type"},{"attribute":"capacity.policy_anomaly_detail"},{"attribute":"capacity.policy_efficiency_detail"},{"attribute":"power_state"},{"attribute":"protection_type"},{"attribute":"memory_overcommit"},{"attribute":"ngt.guest_os"},{"attribute":"ngt.enabled_applications"},{"attribute":"ngt.cluster_version"},{"attribute":"node_name"},{"attribute":"is_agent_vm"},{"attribute":"volume_group"},{"attribute":"protection_policy_state"},{"attribute":"recovery_plan_state_list"},{"attribute":"cbr_not_capable_reason"},{"attribute":"ngt.communication_active"},{"attribute":"ngt.communication_over_serial_port_active"},{"attribute":"vpc_name"}],"filter_criteria":"(platform_type!=aws,platform_type==[no_val]);(category_name==@@{CategoryName}@@;category_value==@@{CategoryValue}@@);is_cvm==0"}
method = 'POST'

resp = urlreq(url, verb='POST', auth='BASIC', user=api_user, passwd=api_pass, params=json.dumps(payload), headers=headers)
if resp.ok:
    result = json.loads(resp.content)
    #print result['filtered_entity_count']
    if result['filtered_entity_count'] <= 375:
      print("SUCCESS: The current entity count for category {}:{} is {}").format('@@{CategoryName}@@','@@{CategoryValue}@@',result['filtered_entity_count'])
      exit(0)
    else:
      print("ERROR: The protection policy limit has been exceeded.")
      exit(1)
else:
    error_details = resp.json()
    print('ERROR: Code {} - {}').format(error_details['code'],error_details['message_list'][0]['message'])
    exit(1)
